css:
.vim-mode.command-mode .cursor,
.vim-mode.operator-pending-mode .cursor,
.vim-mode.visual-mode .cursor,
.vim-mode.command-mode .cursor.blink-off,
.vim-mode.operator-pending-mode .cursor.blink-off,
.vim-mode.visual-mode .cursor.blink-off {
  border: 0;
  background-color: #ffffff;
  visibility: hidden;
  opacity: 0.5;
}
.vim-mode.command-mode.editor.is-focused .cursor,
.vim-mode.operator-pending-mode.editor.is-focused .cursor,
.vim-mode.visual-mode.editor.is-focused .cursor,
.vim-mode.command-mode.editor.is-focused .cursor.blink-off,
.vim-mode.operator-pending-mode.editor.is-focused .cursor.blink-off,
.vim-mode.visual-mode.editor.is-focused .cursor.blink-off {
  border: 0;
  background-color: #ffffff;
  visibility: visible;
  opacity: 0.5;
}
.vim-mode.visual-mode .cursor.hidden-cursor {
  display: block;
}
.command-mode-input .editor.mini {
  background-color: inherit;
  border: none;
  width: 100%;
  font-weight: normal;
  color: #aaaaaa;
  text-shadow: 0 -1px 0 #333333;
  line-height: 1.28;
  cursor: default;
  white-space: nowrap;
  padding-left: 10px;
}
.command-mode-input .hidden-input {
  padding: 0;
  border: 0;
  position: absolute;
  z-index: -1;
  top: 0;
  left: 0;
  opacity: 0;
  width: 1px;
}
