css:
.lint {
  position: absolute;
  width: 100%;
  height: 100%;
}
.lint .violation {
  position: absolute;
}
.lint .violation-arrow {
  position: absolute;
  left: 0;
  bottom: 0;
  width: 0;
  height: 0;
  border-style: solid;
  border-color: transparent;
  border-top-width: 0;
}
.lint .violation-arrow.violation-warning {
  border-bottom-color: #ff982d;
}
.lint .violation-arrow.violation-error {
  border-bottom-color: #cc0000;
}
.lint .violation-area {
  position: absolute;
  top: 0;
}
.lint .violation-border {
  border-bottom-style: dotted;
  border-bottom-width: 1px;
}
.lint .violation-border.violation-warning {
  border-bottom-color: rgba(255, 152, 45, 0.8);
}
.lint .violation-border.violation-error {
  border-bottom-color: rgba(204, 0, 0, 0.8);
}
.lint .tooltip {
  font-family: 'Lucida Grande', 'Segoe UI', sans-serif;
  max-width: 430px;
}
.lint .tooltip.top-right {
  margin-top: -1px;
  padding: 5px 0;
}
.lint .tooltip.top-right .tooltip-arrow {
  border-bottom-width: 0 !important;
}
.lint .tooltip.top-right .tooltip-arrow {
  left: 0 !important;
  border-left-width: 0 !important;
}
.lint .tooltip.top-right .tooltip-inner {
  border-bottom-left-radius: 0 !important;
}
.lint .tooltip.bottom-right {
  margin-top: 1px;
  padding: 5px 0;
}
.lint .tooltip.bottom-right .tooltip-arrow {
  border-top-width: 0 !important;
}
.lint .tooltip.bottom-right .tooltip-arrow {
  left: 0 !important;
  border-left-width: 0 !important;
}
.lint .tooltip.bottom-right .tooltip-inner {
  border-top-left-radius: 0 !important;
}
.lint .tooltip.bottom-left {
  margin-top: 1px;
  padding: 5px 0;
}
.lint .tooltip.bottom-left .tooltip-arrow {
  border-top-width: 0 !important;
}
.lint .tooltip.bottom-left .tooltip-arrow {
  left: auto !important;
  right: 0 !important;
  border-right-width: 0 !important;
}
.lint .tooltip.bottom-left .tooltip-inner {
  border-top-right-radius: 0 !important;
}
.lint .tooltip.top-left {
  margin-top: -1px;
  padding: 5px 0;
}
.lint .tooltip.top-left .tooltip-arrow {
  border-bottom-width: 0 !important;
}
.lint .tooltip.top-left .tooltip-arrow {
  left: auto !important;
  right: 0 !important;
  border-right-width: 0 !important;
}
.lint .tooltip.top-left .tooltip-inner {
  border-bottom-right-radius: 0 !important;
}
.lint .tooltip .tooltip-inner {
  white-space: pre-wrap;
  text-align: left;
}
.lint .tooltip .tooltip-inner .info-color {
  display: none;
  background-color: #0098ff;
}
.lint .tooltip .tooltip-inner .attachment {
  margin-top: 5px;
}
.lint .tooltip .tooltip-inner code,
.lint .tooltip .tooltip-inner pre {
  border: 1px solid transparent;
}
.lint .tooltip .tooltip-inner code {
  text-shadow: none;
}
.lint .tooltip .tooltip-inner pre {
  margin: 5px 0;
  padding: 4px 8px;
  white-space: pre;
  word-wrap: normal;
  overflow: scroll;
  font-size: 10px;
  background-color: transparent;
  border: 1px solid transparent;
}
.lint .tooltip .tooltip-inner .block-metadata {
  display: block;
  margin-left: -7px;
}
.lint .tooltip .tooltip-inner .metadata .item {
  margin-left: 7px;
  padding: 2px 4px;
  font-size: 10px;
  font-weight: bold;
  text-shadow: none;
  border-radius: 2px;
  color: #ffffff;
  background-color: #2092df;
}
.editor .gutter .line-number.lint-warning .icon-right {
  visibility: visible;
}
.editor .gutter .line-number.lint-warning .icon-right:before {
  content: "\f02d";
  color: #ff982d;
}
.editor .gutter .line-number.lint-error .icon-right {
  visibility: visible;
}
.editor .gutter .line-number.lint-error .icon-right:before {
  content: "\f02d";
  color: #cc0000;
}
.status-bar .lint-status .icon {
  margin-left: 5px;
}
.status-bar .lint-status .icon:before {
  width: 12px;
  height: 12px;
  font-size: 12px;
  top: 0;
  margin-right: 2px;
}
.status-bar .lint-status .lint-clean:before {
  color: #529b2f;
}
.status-bar .lint-status .lint-warning:before {
  color: #ff982d;
}
.status-bar .lint-status .lint-error:before {
  color: #cc0000;
}
