/* FONT PATH
 * -------------------------- */
@font-face {
  font-family: 'FontAwesome';
  src: url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.eot?v=4.0.3');
  src: url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.eot?#iefix&v=4.0.3') format('embedded-opentype'), url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.woff?v=4.0.3') format('woff'), url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.ttf?v=4.0.3') format('truetype'), url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.svg?v=4.0.3#fontawesomeregular') format('svg');
  font-weight: normal;
  font-style: normal;
}
.gutter.linter-gutter-enabled .line-number:before {
  visibility: hidden;
  position: relative;
  bottom: .19em;
  margin-right: .6em;
  content: "\f111";
  font-size: 0.7em;
  display: inline-block;
  font-family: FontAwesome;
  font-style: normal;
  font-weight: normal;
  -webkit-font-smoothing: antialiased;
}
.gutter.linter-gutter-enabled .line-number.linter-warning:before {
  visibility: visible !important;
  color: #daa520;
}
.gutter.linter-gutter-enabled .line-number.linter-error:before {
  visibility: visible !important;
  color: #b22222;
}
.linter-statusbar {
  max-height: 140px;
  overflow-y: scroll;
  margin-bottom: 0;
}
.linter-statusbar dd:before,
.linter-statusbar dd:after {
  content: " ";
  display: table;
}
.linter-statusbar dd:after {
  clear: both;
}
.linter-statusbar dt {
  float: left;
  width: 70px;
  clear: left;
  text-align: right;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-weight: normal !important;
}
.linter-statusbar dd {
  padding-left: 25px;
  margin-left: 50px;
}
.linter-statusbar dd .error-message {
  cursor: pointer;
}
.linter-statusbar dd .pos {
  color: rgba(255, 255, 255, 0.2);
  padding: 2px;
  border-radius: 1px 2px;
}
.highlight.linter-warning .region {
  background: linear-gradient(to bottom, #daa520 0%, rgba(218, 165, 32, 0.30000000000000004) 20%, rgba(218, 165, 32, 0.30000000000000004) 80%, #daa520 100%);
  border-left: 2px solid #daa520;
  border-right: 2px solid #daa520;
}
.highlight.linter-error .region {
  background: linear-gradient(to bottom, #b22222 0%, rgba(178, 34, 34, 0.30000000000000004) 20%, rgba(178, 34, 34, 0.30000000000000004) 80%, #b22222 100%);
  border-left: 2px solid #b22222;
  border-right: 2px solid #b22222;
}
